CREATE OR REPLACE FUNCTION qt_obter_convenio_agendas (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_convenio_w	varchar(255);
cd_convenio_w	bigint;


BEGIN

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then

	select  max(cd_convenio)
	into STRICT	cd_convenio_w
	from	paciente_setor_convenio
	where	nr_seq_paciente = nr_seq_paciente_p;

	if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then
		ds_convenio_w := substr(obter_desc_convenio(cd_convenio_w),1,80);
	end if;
end if;


return	ds_convenio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
