SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qt_obter_cor_autor_pend (nr_seq_atendimento_p bigint, ie_cor_p text) RETURNS varchar AS $body$
DECLARE


ie_status_autor_w	varchar(1);
nr_seq_estagio_w	bigint;
nr_ciclo_w		smallint;
ds_dia_ciclo_w		varchar(5);
ds_cor_w		varchar(15);
ds_cor_fonte_w		varchar(15);
ds_estagio_w		varchar(60);
nr_seq_paciente_w	bigint;
nr_seq_estagio_char_w	varchar(15);

ds_cor_retorno_w	varchar(60);
nr_seq_autorizacao_w	bigint;


BEGIN

ie_status_autor_w := obter_param_usuario(865, 118, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_status_autor_w);

if (ie_status_autor_w = 'S') then
	select	max(nr_seq_paciente),
		max(nr_ciclo),
		max(ds_dia_ciclo)
	into STRICT	nr_seq_paciente_w,
		nr_ciclo_w,
		ds_dia_ciclo_w
	from	paciente_atendimento
	where	nr_seq_atendimento	= nr_seq_atendimento_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_autorizacao_w
	from	autorizacao_convenio
	where	nr_seq_paciente = nr_seq_atendimento_p;

if (coalesce(nr_seq_autorizacao_w::text, '') = '') then
	begin

	select	nr_ciclo
	into STRICT	nr_ciclo_w
	from	paciente_atendimento
	where	nr_seq_atendimento = nr_seq_atendimento_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_autorizacao_w
	from	autorizacao_convenio
	where	nr_seq_paciente_setor = nr_seq_paciente_w
	and	nr_ciclo = nr_ciclo_w;
end;
end if;

if (nr_seq_autorizacao_w IS NOT NULL AND nr_seq_autorizacao_w::text <> '') then
		select	max(nr_seq_estagio),
			to_char(max(nr_seq_estagio))
		into STRICT	nr_seq_estagio_w,
			nr_seq_estagio_char_w
		from	autorizacao_convenio
		where	nr_sequencia = nr_seq_autorizacao_w;

end if;

	if (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then
		select	substr(max(ds_cor),1,15),
			substr(max(ds_cor_fonte),1,15),
			substr(max(ds_estagio),1,60)
		into STRICT	ds_cor_w,
			ds_cor_fonte_w,
			ds_estagio_w
		from	estagio_autorizacao
		where	nr_sequencia = nr_seq_estagio_w
		and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;


		if (ie_cor_p = 'B') then
			ds_cor_retorno_w := ds_cor_w;
		elsif (ie_cor_p = 'F') then
			ds_cor_retorno_w := ds_cor_fonte_w;
		elsif (ie_cor_p = 'W') then
			ds_cor_retorno_w := ds_estagio_w;
		elsif (ie_cor_p = 'N') then
			ds_cor_retorno_w := nr_seq_estagio_char_w;
		end if;
	end if;
end if;

return	ds_cor_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
