CREATE OR REPLACE FUNCTION qt_obter_cor_classif_pessoa (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);

C01 CURSOR FOR
		SELECT	a.ds_cor
		from    classif_pessoa a,
				pessoa_classif	b,
				paciente_setor c
		where 	b.nr_seq_classif = a.nr_sequencia
		and		clock_timestamp()	between b.dt_inicio_vigencia and coalesce(b.dt_final_vigencia,clock_timestamp() + interval '1 days')
		and 	c.cd_pessoa_fisica = b.cd_pessoa_fisica
		and		c.nr_seq_paciente  = nr_seq_paciente_p
		and		coalesce(a.ie_exibe_quimioterapia,'N') = 'S'
		and		coalesce(a.ie_situacao,'A') = 'A'
		order by b.dt_inicio_vigencia;


BEGIN
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w := ds_retorno_w;
		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
