CREATE OR REPLACE FUNCTION qt_obter_dados_agequi ( nr_seq_agequi_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
/*
	P - CÃ³digo pessoa fisica
	L - nr seq local
*/
BEGIN

if (nr_seq_agequi_p	> 0) then
	if (ie_opcao_p	= 'P') then
		select	max(cd_pessoa_fisica)
		into STRICT	ds_retorno_w
		from	agenda_quimio
		where	nr_sequencia	= nr_seq_agequi_p;
	elsif (ie_opcao_p	= 'L') then
		select	max(nr_seq_local)
		into STRICT	ds_retorno_w
		from	paciente_atendimento
		where	nr_seq_Atendimento	= nr_seq_agequi_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
