CREATE OR REPLACE FUNCTION qt_obter_dados_hor_item (nr_seq_horario_p bigint, ie_tipo_item_p text, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_horario_w	timestamp;


BEGIN
    begin
	if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
		if (ie_tipo_item_p = 'D') then
			select	dt_horario
			into STRICT	dt_horario_w
			from	prescr_dieta_hor
			where	nr_sequencia	= nr_seq_horario_p;

		elsif (ie_tipo_item_p in ('S','M','MAT','IAG','IA', 'LD')) then
			select	dt_horario
			into STRICT	dt_horario_w
			from	prescr_mat_hor
			where	nr_sequencia	= nr_seq_horario_p;

		elsif (ie_tipo_item_p in ('P','G','C','I')) then
			select	dt_horario
			into STRICT	dt_horario_w
			from	prescr_proc_hor
			where	nr_sequencia	= nr_seq_horario_p;

		elsif (ie_tipo_item_p = 'R') then
			select	dt_horario
			into STRICT	dt_horario_w
			from	prescr_rec_hor
			where	nr_sequencia	= nr_seq_horario_p;

		elsif (ie_tipo_item_p = 'E') then
			select	dt_horario
			into STRICT	dt_horario_w
			from	pe_prescr_proc_hor
			where	nr_sequencia	= nr_seq_horario_p;
		end if;
	end if;
    end;
return dt_horario_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
