CREATE OR REPLACE FUNCTION qt_obter_dados_marcacao ( nr_seq_pend_quimio_p bigint, dt_agenda_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
nm_prof_w	varchar(60);	
dt_Agenda_w	timestamp;		
nr_seq_prof_w	bigint;
			

BEGIN 
 
select	max(dt_agenda), 
	max(nr_seq_prof) 
into STRICT	dt_Agenda_w, 
	nr_Seq_prof_w 
from	agenda_quimio_marcacao 
where	nr_seq_pend_agenda	= nr_seq_pend_quimio_p 
and	trunc(dt_agenda)	= trunc(dt_agenda_p);
 
if (ie_opcao_p	= 'DT') then 
	ds_retorno_w	:= dt_agenda_w;
else 
	select	max(SUBSTR(OBTER_NOME_PF(b.CD_PESSOA_FISICA),0,60)) 
	into STRICT	nm_prof_w 
	from	qt_profissional a, 
		pessoa_fisica b 
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
	and	a.nr_sequencia	= nr_seq_prof_w;
	 
	ds_retorno_w	:= nm_prof_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
