CREATE OR REPLACE FUNCTION qt_obter_dados_pac_atend (nr_seq_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_ciclo_w	smallint;
ds_dia_ciclo_w	varchar(5);
nr_seq_motivo_susp_w	bigint;
ds_motivo_w	varchar(255);


BEGIN

select	max(nr_ciclo),
	max(ds_dia_ciclo),
	max(nr_seq_motivo_susp)
into STRICT	nr_ciclo_w,
	ds_dia_ciclo_w,
	nr_seq_motivo_susp_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;

select	max(ds_motivo)
into STRICT	ds_motivo_w
from	motivo_suspensao_qumio
where	nr_sequencia = nr_seq_motivo_susp_w;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= nr_ciclo_w;
elsif (ie_opcao_p = 'DC') then
	ds_retorno_w	:= ds_dia_ciclo_w;
elsif (ie_opcao_p = 'MS') then
	ds_retorno_w	:= ds_motivo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
