SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qt_obter_dados_sim (nr_seq_paciente_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_pessoa_fisica_w	varchar(10);
ds_protocolo_w		varchar(255);
qt_tempo_medic_w	bigint;


BEGIN


select	max(cd_pessoa_fisica),
	coalesce(max(obter_desc_protocolo(cd_protocolo)),' ')||' / '||coalesce(max(obter_desc_protocolo_medic(nr_seq_medicacao, cd_protocolo)),' '),
	max(qt_tempo_medic)
into STRICT	cd_pessoa_fisica_w,
	ds_protocolo_w,
	qt_tempo_medic_w
from	paciente_setor_sim
where	nr_sequencia = nr_seq_paciente_p;

if (ie_opcao_p	= 'P') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p	= 'PR') then
	ds_retorno_w	:= ds_protocolo_w;
elsif (ie_opcao_p	= 'T') then
	ds_retorno_w	:= qt_tempo_medic_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
