SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qt_obter_data_status (ie_status_p text, nr_seq_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;
ie_falta_w	varchar(1);

BEGIN

if (ie_status_p = 'E') then
	select	max(dt_executada)
	into STRICT	dt_retorno_w
	from	agenda_quimio
	where	nr_seq_atendimento = nr_seq_atendimento_p
	and	nr_sequencia	= (	SELECT 	max(nr_sequencia)
					from	agenda_quimio
					where	nr_seq_atendimento	= nr_seq_atendimento_p);
elsif (ie_status_p = 'F') then
	select 	coalesce(max(IE_FALTA_PENDENCIA),'N')
	into STRICT 	ie_falta_w
	from 	PARAMETRO_AGENDA_QUIMIO
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

	select	max(dt_falta)
	into STRICT	dt_retorno_w
	from	agenda_quimio
	where	nr_seq_atendimento = nr_seq_atendimento_p
	and 	(((ie_falta_w = 'N')
	and 	nr_sequencia = ( SELECT	max(nr_sequencia)
					from	agenda_quimio
					where	nr_seq_atendimento	= nr_seq_atendimento_p))
	or (ie_falta_w = 'S'));

elsif (ie_status_p = 'A') then
	select	max(dt_falta)
	into STRICT	dt_retorno_w
	from	agenda_quimio
	where	nr_seq_atendimento = nr_seq_atendimento_p
	and	nr_sequencia	= (	SELECT 	max(nr_sequencia)
					from	agenda_quimio
					where	nr_seq_atendimento	= nr_seq_atendimento_p);

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
