CREATE OR REPLACE FUNCTION qt_obter_desc_item (nr_seq_item_p bigint, nr_seq_atendimento_p bigint, dt_prevista_quimio_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	select	max(nr_ciclo||' - '||ds_dia_ciclo||' - '||to_char(coalesce(dt_prevista_agenda, dt_prevista),'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	ds_retorno_w
	from	paciente_atendimento
	where	nr_seq_atendimento = nr_seq_atendimento_p;
elsif (dt_prevista_quimio_p IS NOT NULL AND dt_prevista_quimio_p::text <> '') then
	select	max(nr_ciclo||' - '||ds_dia_ciclo||' - '||to_char(dt_prevista_quimio,'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	ds_retorno_w
        from	agenda_integrada_item
	where	nr_sequencia = nr_seq_item_p;
else
	select	max(ds_item)
	into STRICT	ds_retorno_w
	from	agenda_integrada_item a,
		qt_item_agenda b
	where	a.ie_tipo_pend_agenda = b.cd_item
	and	a.nr_sequencia = nr_seq_item_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
