SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qt_obter_desc_prof_local ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(60);
			

BEGIN 
if (ie_opcao_p	= 'P') then 
	select	substr(max(OBTER_NOME_PF(b.CD_PESSOA_FISICA)),1,60) 
	into STRICT	ds_retorno_w 
	from	pessoa_fisica b, 
		qt_profissional a 
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
	and	a.nr_sequencia		= nr_sequencia_p;
else 
	select	substr(max(ds_local),1,60) 
	into STRICT	ds_retorno_w 
	from	qt_local 
	where	nr_sequencia	= nr_sequencia_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
