CREATE OR REPLACE FUNCTION qt_obter_desc_prot ( nr_seq_pend_agenda_p bigint, nr_seq_atend_sim_p text, cd_protocolo_p bigint, nr_seq_medicamento_p bigint, nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_protocolo_sim_w	varchar(255);
ds_medic_sim_w		varchar(255);
ds_protocolo_w      varchar(255);
ds_medicamento_w    varchar(255);


BEGIN 
 
if (coalesce(nr_seq_atend_sim_p::text, '') = '') then
    begin
        select  obter_desc_protocolo(a.cd_protocolo),
                obter_desc_protocolo_medic(a.nr_seq_medicacao, a.cd_protocolo)
        into STRICT    ds_protocolo_w,
                ds_medicamento_w		
        from    paciente_setor a,
                paciente_atendimento b
        where	a.nr_seq_paciente	= b.nr_seq_paciente
        and	    b.nr_seq_atendimento = nr_seq_atendimento_p;
    exception
    when no_data_found  then
        ds_protocolo_w := null;
        ds_medicamento_w := null;
    when too_many_rows  then
        ds_protocolo_w := null;
        ds_medicamento_w := null;
    end;

    if ((coalesce(ds_protocolo_w::text, '') = '' or coalesce(ds_medicamento_w::text, '') = '') and coalesce(cd_protocolo_p::text, '') = '') then
        ds_retorno_w	:= ' ';
    elsif (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') then
        ds_retorno_w	:= null;
    else
        ds_retorno_w	:= ds_protocolo_w ||' / '||ds_medicamento_w;
    end if;

else 
	select	substr(coalesce(max(obter_desc_protocolo(c.cd_protocolo)),' '),1,255), 
			substr(coalesce(max(obter_desc_protocolo_medic(c.nr_seq_medicacao, c.cd_protocolo)),' '),1,255) 
	into STRICT	ds_protocolo_sim_w, 
			ds_medic_sim_w 
	from	paciente_setor_sim c, 
			paciente_atendimento_sim a 
	where	a.nr_seq_paciente	= c.nr_sequencia 
	and		a.nr_sequencia		= nr_seq_atend_sim_p;
	
	 
	ds_retorno_w	:= ds_protocolo_sim_w ||' / '||ds_medic_sim_w;
end if;
 
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
