CREATE OR REPLACE FUNCTION qt_obter_dose_total_ordem (cd_material_p bigint, nr_seq_ordem_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	double precision;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then

	select 	sum(a.qt_dose)
	into STRICT	qt_retorno_w
	from	material	b,
			can_ordem_item_prescr a
	where	a.cd_material	=	b.cd_material
	and		a.nr_seq_ordem 	=	nr_seq_ordem_p
	and		b.nr_seq_ficha_tecnica	in	substr((SELECT	x.nr_seq_ficha_tecnica
						 from	material x
						 where	x.cd_material = cd_material_p),1,10);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
