CREATE OR REPLACE FUNCTION qt_obter_ds_obs_ageint (nr_seq_pend_agenda_p agenda_quimio.nr_seq_pend_agenda%type, nr_seq_agenda_p agenda_quimio.nr_seq_pend_agenda%type) RETURNS varchar AS $body$
DECLARE


    nr_seq_ageint_w     agenda_integrada_item.nr_seq_agenda_int%type;
    ds_retorno_w        varchar(2000);


BEGIN
    if (nr_seq_pend_agenda_p IS NOT NULL AND nr_seq_pend_agenda_p::text <> '') then
        select  max(nr_seq_agenda_int)
        into STRICT    nr_seq_ageint_w
        from    agenda_integrada_item
        where   nr_seq_pend_quimio = nr_seq_pend_agenda_p;

        if (coalesce(nr_seq_ageint_w,0) > 0) then
            select  substr(max(ds_obs_final),1,2000)
            into STRICT    ds_retorno_w
            from    agenda_integrada
            where   nr_sequencia = nr_seq_ageint_w;
        end if;

    elsif (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
        select  max(nr_seq_agenda_int)
            into STRICT    nr_seq_ageint_w
            from    agenda_integrada_item
            where   nr_seq_agequi = nr_seq_agenda_p;

        if (coalesce(nr_seq_ageint_w,0) > 0) then
            select  substr(max(ds_obs_final),1,2000)
            into STRICT    ds_retorno_w
            from    agenda_integrada
            where   nr_sequencia = nr_seq_ageint_w;
        end if;
    end if;

	return ds_retorno_w;
end;
