CREATE OR REPLACE FUNCTION qt_obter_dt_agendado_item (nr_seq_item_p bigint, dt_agenda_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	select	max(dt_agenda)
	into STRICT	dt_retorno_w
	from	agenda_quimio_marcacao
	where	nr_seq_ageint_item	= nr_seq_item_p
	and	trunc(dt_agenda)	= trunc(dt_agenda_p);
else
	select	max(dt_agenda)
	into STRICT	dt_retorno_w
	from	agenda_quimio_marcacao
	where	nr_seq_ageint_item	= nr_seq_item_p;
end if;

return	dt_retorno_w;

end;
$body$
