CREATE OR REPLACE FUNCTION qt_obter_dur_forcar_agend (nr_seq_pendencia_p bigint, dt_agenda_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;
nr_seq_atendimento_w	bigint;
ds_dia_ciclo_w		varchar(5);
cd_protocolo_w		bigint;
nr_seq_prot_medic_w	integer;


BEGIN

select	max(b.nr_seq_atendimento),
	max(b.ds_dia_ciclo),
	max(a.cd_protocolo),
	max(a.nr_seq_medicacao)
into STRICT	nr_seq_atendimento_w,
	ds_dia_ciclo_w,
	cd_protocolo_w,
	nr_seq_prot_medic_w
from	paciente_setor a,
	paciente_atendimento b
where	b.nr_seq_pend_agenda	= nr_seq_pendencia_p
and	a.nr_seq_paciente	= b.nr_seq_paciente
and	coalesce(b.dt_suspensao::text, '') = ''
and	coalesce(b.dt_cancelamento::text, '') = ''
and	trunc(coalesce(b.dt_real, b.dt_prevista))	= trunc(dt_agenda_p);

if (nr_seq_atendimento_w IS NOT NULL AND nr_seq_atendimento_w::text <> '') and (ds_dia_ciclo_w IS NOT NULL AND ds_dia_ciclo_w::text <> '') and (cd_protocolo_w IS NOT NULL AND cd_protocolo_w::text <> '') and (nr_seq_prot_medic_w IS NOT NULL AND nr_seq_prot_medic_w::text <> '') then

	qt_retorno_w	:= qt_obter_dur_aplicacao(ds_dia_ciclo_w,
						nr_seq_prot_medic_w,
						cd_protocolo_w,
						nr_seq_atendimento_w,
						dt_agenda_p,
						nr_seq_pendencia_p,
						nm_usuario_p,
						cd_estabelecimento_p);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
