CREATE OR REPLACE FUNCTION qt_obter_grupo_local_age (nr_seq_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_local_w	bigint;


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

	select	max(nr_seq_local)
	into STRICT	nr_seq_local_w
	from	agenda_quimio
	where 	nr_seq_atendimento = nr_seq_atendimento_p
	and ie_status_agenda <> 'C';

	if (ie_opcao_p = 'C') then

		ds_retorno_w :=  substr(obter_qt_nr_grupo_local(nr_seq_local_w),1,250);

	elsif (ie_opcao_p = 'D') then

		ds_retorno_w  :=   substr(obter_qt_grupo_local(nr_seq_local_w),1,250);
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
