CREATE OR REPLACE FUNCTION qt_obter_idade_pac_agenda ( cd_tipo_agenda_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
qt_idade_agenda_w	smallint;
qt_idade_meses_w	bigint;
dt_nascimento_w		timestamp;
dt_atual_w		timestamp;
ds_idade_pf_w		varchar(30);
ds_idade_w		varchar(255);


BEGIN
if (cd_tipo_agenda_p IS NOT NULL AND cd_tipo_agenda_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	if (cd_tipo_agenda_p = 2) then
		/* obter dados agenda */

		select	cd_pessoa_fisica
		into STRICT	cd_pessoa_fisica_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_agenda_p;

		/* calcular / gerar idade */

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			/* obter dados pf */

			select	coalesce(max(dt_nascimento), clock_timestamp()),
				coalesce(max(dt_obito), clock_timestamp())
			into STRICT	dt_nascimento_w,
				dt_atual_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;

			/* obter idade */

			select	obter_idade(dt_nascimento_w, dt_atual_w, 'A')
			into STRICT	ds_idade_pf_w
			;

			if (somente_numero(ds_idade_pf_w) > 0) then
				ds_idade_w := ds_idade_pf_w;
			end if;
		end if;

	elsif (cd_tipo_agenda_p = 3) then
		/* obter dados agenda */

		select	cd_pessoa_fisica
		into STRICT	cd_pessoa_fisica_w
		from	agenda_consulta
		where	nr_sequencia = nr_seq_agenda_p;

		/* calcular / gerar idade */

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			/* obter dados pf */

			select	coalesce(max(dt_nascimento), clock_timestamp()),
				coalesce(max(dt_obito), clock_timestamp())
			into STRICT	dt_nascimento_w,
				dt_atual_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;

			/* obter idade */

			select	obter_idade(dt_nascimento_w, dt_atual_w, 'A')
			into STRICT	ds_idade_pf_w
			;

			if (somente_numero(ds_idade_pf_w) > 0) then
				ds_idade_w := ds_idade_pf_w;
			end if;
		end if;
	end if;
end if;

return ds_idade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
