CREATE OR REPLACE FUNCTION qt_obter_numero_apac ( cd_pessoa_fisica_p text, dt_tratamento_p timestamp) RETURNS bigint AS $body$
DECLARE

 
nr_Retorno_w	bigint;				
				 

BEGIN 
 
select	max(nr_apac) 
into STRICT	nr_Retorno_w 
from	sus_laudo_paciente a, 
		atendimento_paciente b 
where	a.nr_atendimento	= b.nr_atendimento 
and		coalesce(a.dt_cancelamento::text, '') = '' 
and		b.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and (dt_tratamento_p	>= a.dt_inicio_val_apac or coalesce(a.dt_inicio_val_apac::text, '') = '') 
and (dt_tratamento_p	<= a.dt_fim_val_apac or coalesce(a.dt_fim_val_apac::text, '') = '');
 
return	nr_Retorno_w;
 
end;
