CREATE OR REPLACE FUNCTION qt_obter_seq_agenda_quimio (nr_seq_atendimento_p paciente_atendimento.nr_seq_atendimento%type) RETURNS AGENDA_QUIMIO.NR_SEQUENCIA%TYPE AS $body$
DECLARE


nr_sequencia_w agenda_quimio.nr_sequencia%type;


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

  select max(nr_sequencia)
  into STRICT nr_sequencia_w
  from agenda_quimio
  where nr_seq_atendimento = nr_seq_atendimento_p 
  and coalesce(dt_cancelada::text, '') = '';

end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
