CREATE OR REPLACE FUNCTION qt_obter_se_dia_livre ( dt_tratamento_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);
nr_seq_local_w	bigint;
ie_feriado_w	varchar(1);
ie_dia_Semana_w	integer;
qt_bloqueio_w	bigint;
qt_turno_w		bigint;
ie_bloqueio_w	varchar(1) := 'S';

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	qt_local
	where	ie_situacao	= 'A'
	and		cd_estabelecimento	= cd_estabelecimento_p
	order by 1;


BEGIN

ie_dia_semana_w	:= obter_cod_dia_semana(dt_tratamento_p);

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_feriado_w
from 	feriado a
where 	a.cd_estabelecimento 	= cd_estabelecimento_p
and	a.dt_feriado between trunc(dt_tratamento_p) and trunc(dt_tratamento_p) + 86399/86400;

open C01;
loop
fetch C01 into
	nr_seq_local_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_bloqueio_w = 'S') then
		select	count(*)
		into STRICT	qt_turno_w
		from	qt_local_turno a
		where	a.nr_seq_local	= nr_seq_local_w
		and	((a.dt_dia_semana = ie_dia_semana_w) or ((a.dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))))
		and	((ie_feriado_w	<> 'S' and coalesce(a.ie_feriado,'N') = 'N') or (coalesce(a.ie_feriado, 'N') = 'S' and ie_feriado_w = 'S'))
		and	((coalesce(a.dt_vigencia_inicial::text, '') = '') or (a.dt_vigencia_inicial <= clock_timestamp()))
		and	((coalesce(a.dt_vigencia_final::text, '') = '') or (a.dt_vigencia_final   >= clock_timestamp()))

		;

		if (qt_turno_w	> 0) then
			select	count(*)
			into STRICT	qt_bloqueio_w
			from	qt_local_turno a
			where	a.nr_seq_local	= nr_seq_local_w
			and	((a.dt_dia_semana = ie_dia_semana_w) or ((a.dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))))
			and	((ie_feriado_w	<> 'S' and coalesce(a.ie_feriado,'N') = 'N') or (coalesce(a.ie_feriado, 'N') = 'S' and ie_feriado_w = 'S'))
			and	((coalesce(a.dt_vigencia_inicial::text, '') = '') or (a.dt_vigencia_inicial <= clock_timestamp()))
			and	((coalesce(a.dt_vigencia_final::text, '') = '') or (a.dt_vigencia_final   >= clock_timestamp()))
			and exists 	(		SELECT	1
							from	qt_bloqueio b
							where	b.nr_seq_local 	= a.nr_seq_local
							and		trunc(dt_tratamento_p) between b.dt_inicial  and b.dt_final
							and		(((b.hr_inicio_bloqueio - trunc(b.hr_inicio_bloqueio))::numeric  <= (hr_inicial - trunc(hr_inicial))::numeric ) or coalesce(b.hr_inicio_bloqueio::text, '') = '')
							and		(((b.hr_final_bloqueio  - trunc(b.hr_final_bloqueio))::numeric   >= (hr_final - trunc(hr_final))::numeric ) or coalesce(b.hr_final_bloqueio::text, '') = '')
							and		(((b.dt_dia_semana = ie_dia_semana_w) or ((b.dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1)))) or (coalesce(dt_dia_semana::text, '') = ''))
					);
		end if;
		if (qt_bloqueio_w	> 0) or (qt_turno_w = 0) then
			ds_retorno_w	:= to_char(dt_tratamento_p,'dd/mm/yyyy') || ', ';
		else
			ds_retorno_w	:= '';
			ie_bloqueio_w	:= 'N';
		end if;
	end if;
	end;
end loop;
close C01;

return	substr(ds_retorno_w, 1, length(ds_retorno_w) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
