CREATE OR REPLACE FUNCTION qt_obter_se_horario_concor ( nr_seq_pend_agenda_p bigint, hr_agenda_p timestamp, nm_usuario_p text, cd_Estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
cd_pessoa_fisica_w	varchar(10);
qt_marcado_w		integer;
qt_marcado_exame_w	integer;
qt_marcado_cons_w	integer;
qt_tempo_medic_w	bigint;
qt_tempo_prep_medic_w	bigint;
qt_tempo_prep_pac_w	bigint;
qt_marcado_radio_w	bigint;
nr_seq_atendimento_w	bigint;
ie_tempo_medic_w	varchar(1);
ie_cons_agenda_w	varchar(1);


BEGIN

select	coalesce(max(Obter_Valor_Param_Usuario(865, 1, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)),0)
into STRICT	qt_tempo_prep_medic_w
;

select	coalesce(max(Obter_Valor_Param_Usuario(865, 2, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)),0)
into STRICT	qt_tempo_prep_pac_w
;

select	coalesce(max(Obter_Valor_Param_Usuario(865, 24, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)),0)
into STRICT	ie_tempo_medic_w
;

ie_cons_agenda_w := Obter_param_Usuario(865, 234, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_cons_agenda_w);

select	max(cd_pessoa_fisica),
	max(CASE WHEN ie_tempo_medic_w='N' THEN  a.qt_tempo_medic WHEN ie_tempo_medic_w='C' THEN  qt_obter_tempo_dur_dia(b.ds_dia_ciclo,a.nr_seq_medicacao,a.cd_protocolo,b.nr_ciclo)  ELSE qt_obter_tempo_dia(b.nr_seq_atendimento) END ),
	max(b.nr_seq_atendimento)
into STRICT	cd_pessoa_fisica_w,
	qt_tempo_medic_w,
	nr_seq_atendimento_w
from	paciente_setor a,
	paciente_atendimento b
where	a.nr_seq_paciente			= b.nr_seq_paciente
and	b.nr_seq_pend_agenda			= nr_seq_pend_agenda_p
and	coalesce(trunc(dt_real), trunc(dt_prevista))	= trunc(hr_agenda_p);

qt_tempo_medic_w	:= qt_tempo_medic_w + qt_tempo_prep_medic_w + qt_tempo_prep_pac_w;

/*select	count(*)
into	qt_marcado_w
from	agenda_quimio_marcacao
where	((hr_agenda_p + 1/ 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440)
or	(hr_agenda_p + qt_tempo_medic_w - 1 / 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440))
and	cd_pessoa_fisica	= cd_pessoa_fisica_w;*/
select	count(*)
into STRICT	qt_marcado_w
from	agenda_quimio
where	((hr_agenda_p + 1/ 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440)
or	(hr_agenda_p + (qt_tempo_medic_w - 1) / 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440))
and	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	ie_status_agenda	<> 'C'
and	ie_status_agenda	<> 'S'
and	nr_seq_atendimento	<> nr_Seq_atendimento_w;

select	count(*)
into STRICT	qt_marcado_exame_w
from	agenda_paciente
where	((hr_agenda_p + 1/ 1440 between hr_inicio and hr_inicio + nr_minuto_duracao / 1440)
or	(hr_agenda_p + (qt_tempo_medic_w - 1) / 1440 between hr_inicio and hr_inicio + nr_minuto_duracao / 1440))
and	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	ie_Status_Agenda <> 'C';

select	count(*)
into STRICT	qt_marcado_cons_w
from	agenda_consulta
where	((hr_agenda_p + 1/ 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440)
or	(hr_agenda_p + (qt_tempo_medic_w - 1) / 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440))
and	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	ie_Status_Agenda <> 'C';

select	count(*)
into STRICT	qt_marcado_radio_w
from	rxt_agenda
where	((hr_agenda_p + 1/ 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440)
or	(hr_agenda_p + (qt_tempo_medic_w - 1) / 1440 between dt_agenda and dt_agenda + nr_minuto_duracao / 1440))
and	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	ie_Status_Agenda <> 'C';

if (ie_cons_agenda_w = 'S') and
	((qt_marcado_w		> 0) or (qt_marcado_cons_w 	> 0) or (qt_marcado_exame_w	> 0) or (qt_marcado_radio_w	> 0)) then
	ds_Retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
