CREATE OR REPLACE FUNCTION qt_obter_se_hor_prof ( nr_seq_local_p bigint, cd_pessoa_fisica_p text, dt_agenda_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_prof_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'S';
qt_permite_w	bigint;
nr_seq_prof_w	bigint;


BEGIN

if (coalesce(nr_seq_prof_p::text, '') = '') then
	select	coalesce(max(nr_Seq_prof), 0)
	into STRICT	nr_seq_prof_w
	from	qt_paciente_prof
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
else
	nr_seq_prof_w	:= nr_seq_prof_p;
end if;
if (nr_seq_prof_w	> 0) then
	select	count(*)
	into STRICT	qt_permite_w
	from	qt_prof_local
	where	nr_seq_local	= nr_seq_local_p
	and	nr_seq_prof	= nr_seq_prof_w;

	if (qt_permite_w	> 0) then
		if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
			ds_retorno_w	:= Qt_Obter_Se_Prof_Hor(nr_seq_prof_w, dt_agenda_p, cd_estabelecimento_p);
			if (ds_retorno_w	= 'N') then
				select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
				into STRICT	ds_retorno_w
				from	agenda_quimio_marcacao
				where	nr_seq_local	= nr_seq_local_p
				and		dt_agenda_p	between dt_agenda and dt_agenda +  nr_duracao / 1440
				and		ie_gerado	= 'N'
				and		nm_usuario	= nm_usuario_p;
			end if;
		else
			ds_retorno_w	:= 'S';
		end if;
	else
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
