CREATE OR REPLACE FUNCTION qt_obter_se_marcado ( nr_seq_ageint_p bigint) RETURNS varchar AS $body$
DECLARE


ie_marcado_w		varchar(1);

BEGIN

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_marcado_w
from	agenda_quimio_marcacao a,
		agenda_integrada_item b
where	a.nr_seq_ageint_item = b.nr_sequencia
and		a.ie_gerado	= 'N'
and		b.nr_seq_agenda_int	= nr_seq_ageint_p;

if (ie_marcado_w = 'S') then
	begin
	return ie_marcado_w;
	end;
end if;

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_marcado_w
from	agenda_quimio_marcacao a,
		agenda_integrada_item b
where	a.nr_seq_pend_agenda = b.nr_seq_pend_quimio
and		a.ie_gerado = 'N'
and		b.nr_seq_agenda_int	= nr_seq_ageint_p;

return	ie_marcado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
