CREATE OR REPLACE FUNCTION qt_obter_se_perm_conv_agenda ( nr_seq_paciente_p bigint, nr_seq_local_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w			varchar(1)	:= 'S';
cd_estabelecimento_w	smallint;
cd_convenio_w			integer;


BEGIN

select	max(cd_convenio)
into STRICT	cd_convenio_w
from	paciente_setor_convenio
where	nr_seq_paciente	= nr_seq_paciente_p;

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	qt_local
where	nr_sequencia	= nr_seq_local_p;

if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then
	select	coalesce(max(ie_permissao),'S')
	into STRICT	ds_Retorno_w
	from	qt_convenio_estab_regra
	where	cd_convenio				= cd_convenio_w
	and		cd_estabelecimento		= cd_estabelecimento_w
	and		ie_situacao				= 'A';
end if;

return	ds_Retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
