CREATE OR REPLACE FUNCTION qt_obter_se_possui_convenio ( nr_Seq_pend_agenda_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_Retorno_w		varchar(1);
nr_seq_paciente_w	bigint;
qt_convenio_w		bigint;
			

BEGIN 
 
nr_seq_paciente_w	:= Qt_Obter_Dados_Pendencia(nr_seq_pend_agenda_p, 'SP', nm_usuario_p);
 
select	count(*) 
into STRICT	qt_convenio_w 
from	paciente_setor_convenio 
where	nr_seq_paciente	= nr_seq_paciente_w;
 
if (qt_convenio_w	= 0) then 
	ds_retorno_w	:= 'N';
else 
	ds_retorno_w	:= 'S';
end if;
 
return	ds_Retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
