CREATE OR REPLACE FUNCTION qt_obter_se_prim_ciclo_dia (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

	select 	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from   	paciente_atendimento
	where  	nr_ciclo			   =   1
	and	   	ds_dia_ciclo 	   =   'D1'
	and	   	nr_seq_atendimento = nr_seq_atendimento_p;

end if;


return	ds_retorno_w;

end;
