CREATE OR REPLACE FUNCTION qt_obter_se_prim_tratamento (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

select 	coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from	paciente_setor a,
	paciente_atendimento b
where	a.nr_seq_paciente       = 	b.nr_seq_paciente
and	nr_ciclo		=   	1
and	ds_dia_ciclo 	   	=   	'D1'
and	b.nr_seq_atendimento 	=	nr_seq_atendimento_p
and	a.nr_seq_paciente 	= 	(SELECT 	min(x.nr_seq_paciente)
					 from		paciente_setor x
					 where		x.cd_pessoa_fisica 	=	a.cd_pessoa_fisica);


return	ds_retorno_w;

end;
