CREATE OR REPLACE FUNCTION qt_obter_se_prof_hor ( nr_seq_prof_p bigint, dt_agenda_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'N';
ie_feriado_w	varchar(1);
qt_prof_turno_w	bigint;


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_feriado_w
from 	feriado a
where 	a.cd_estabelecimento 	= cd_estabelecimento_p
and	a.dt_feriado between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 86399/86400;

select	count(*)
into STRICT	qt_prof_turno_w
from	qt_prof_turno
where	nr_seq_prof	= nr_seq_prof_p
and	dt_agenda_p between to_date(to_char(dt_agenda_p, 'dd/mm/yyyy') || ' ' || to_char(hr_inicial, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
				and to_date(to_char(dt_agenda_p, 'dd/mm/yyyy') || ' ' || to_char(hr_final, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
and (ie_feriado_w	<> 'S' or (coalesce(ie_feriado, 'S') = 'S' and ie_feriado_w = 'S'));

if (qt_prof_turno_w	> 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
