CREATE OR REPLACE FUNCTION qt_obter_se_prot_alterado ( nr_sequencia_p bigint, nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE

ds_rertorno_w	varchar(1);

BEGIN

	select	coalesce(max('S'),'N')
	into STRICT	ds_rertorno_w
	from   	qt_pendencia_agenda
	where	nr_sequencia =	 nr_sequencia_p
	and	coalesce(ie_alt_medicamento,'N') =  'S';

	if (coalesce(nr_seq_paciente_p,0) > 0) and (coalesce(ds_rertorno_w,'N') = 'N') then

		select	coalesce(max('S'),'N')
		into STRICT	ds_rertorno_w
		from   	paciente_setor
		where	nr_seq_paciente = nr_seq_paciente_p
		and	coalesce(ie_alt_medicamento,'N') =  'S';

	end if;

return	ds_rertorno_w;

end;
