CREATE OR REPLACE FUNCTION qt_obter_se_trat_periodo ( dt_inicial_p timestamp, dt_final_p timestamp, nr_seq_atend_p bigint, ie_tipo_data_p bigint) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w		varchar(1)	:= 'S';
qt_dias_periodo_w	bigint;
nr_seq_paciente_w	bigint;
ie_consiste_dt_w	varchar(1);

BEGIN
ie_consiste_dt_w := Obter_param_Usuario(865, 233, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_consiste_dt_w);

if (coalesce(ie_consiste_dt_w,'N') = 'S') then

	if (ie_tipo_data_p = 0) then
		select	count(*)
		into STRICT	qt_dias_periodo_w
		from	paciente_Atendimento
		where	nr_seq_atendimento = nr_seq_atend_p
		and	coalesce(dt_real, dt_prevista) between dt_inicial_p and dt_final_p + 86399/86400;
	elsif (ie_tipo_data_p = 1) then
		select	count(*)
		into STRICT	qt_dias_periodo_w
		from	paciente_Atendimento
		where	nr_seq_atendimento = nr_seq_atend_p
		and	dt_real between dt_inicial_p and dt_final_p + 86399/86400;
	elsif (ie_tipo_data_p = 2) then
		select	count(*)
		into STRICT	qt_dias_periodo_w
		from	paciente_Atendimento
		where	nr_seq_atendimento = nr_seq_atend_p
		and	dt_prevista between dt_inicial_p and dt_final_p + 86399/86400;
	elsif (ie_tipo_data_p = 3) then
		select	max(nr_seq_paciente)
		into STRICT	nr_seq_paciente_w
		from	paciente_atendimento
		where	nr_seq_atendimento = nr_seq_atend_p;

		select	count(*)
		into STRICT	qt_dias_periodo_w
		from	paciente_setor
		where	nr_seq_paciente = nr_seq_paciente_w
		and	dt_protocolo between dt_inicial_p and trunc(dt_final_p) + 86399/86400;
	end if;

	if (qt_dias_periodo_w	= 0) then
		ds_retorno_w	:= 'N';
	end if;
else

	ds_Retorno_w := 'S';

end if;


return	ds_Retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
