CREATE OR REPLACE FUNCTION qt_obter_status_mapa ( ie_status_quimio_p text, ie_status_param_p text) RETURNS varchar AS $body$
DECLARE




ds_retorno_w	varchar(80)	:= 'N';
qt_regra_status_w	bigint;
ie_status_quimio_w	varchar(80);
qt_Regra_w		bigint;



BEGIN

begin
select	1
into STRICT	qt_regra_status_w
from	qt_status_mapa_ocupado
where	ie_situacao	= 'A'  LIMIT 1;
exception
	when others then
	qt_regra_status_w	:= 0;
end;

if (qt_regra_status_w	= 0) then
	if (ie_status_quimio_p	= ie_status_param_p) then
		ds_retorno_w	:= 'S';
	end if;
else
	select	 count(*)
	into STRICT	 qt_regra_status_w
	from	 qt_status_mapa_ocupado a
	where	 a.ie_status_quimio   = ie_status_quimio_p
	and	 a.ie_situacao = 'A';

	if (qt_regra_status_w > 0) then
		ds_retorno_w		:= 'S';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
