CREATE OR REPLACE FUNCTION qt_obter_status_pendencia ( nr_seq_pend_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


/*
S	- Todos agendados
N	- Nenhum agendado
A	- alguns agendados
*/
			
ds_retorno_w		varchar(1)	:= 'A';
qt_dias_pendencia_w	bigint;
qt_dias_w		bigint;
qt_dias_canc_w		bigint;
qt_dias_susp_medico_w	bigint;
			

BEGIN
/*
select	count(*)
into	qt_dias_pendencia_w
from	paciente_atendimento a
where	nr_seq_pend_agenda	= nr_seq_pend_agenda_p
and		dt_suspensao is null
and		dt_cancelamento is null
and		not exists ( select	1
				from	agenda_quimio b
				where	a.nr_seq_Atendimento		= b.nr_seq_atendimento
				and	nvl(b.ie_Status_agenda, 'X')	<> 'C'
				and	establishment_timezone_utils.startofday(b.dt_Agenda)		= establishment_timezone_utils.startofday(nvl(dt_prevista_agenda, nvl(dt_real, dt_prevista))));
	*/
			
select	count(*)
	into STRICT	qt_dias_pendencia_w
	from	paciente_atendimento a,
			agenda_quimio b
	where	a.nr_seq_pend_agenda	= nr_seq_pend_agenda_p
	and		a.nr_Seq_Atendimento	= b.nr_Seq_Atendimento
	and		coalesce(b.ie_Status_agenda, 'X')	<> 'C'
	and		coalesce(a.dt_suspensao::text, '') = ''
	and		coalesce(a.dt_cancelamento::text, '') = ''
	and		establishment_timezone_utils.startofday(b.dt_Agenda)		= establishment_timezone_utils.startofday(coalesce(a.dt_prevista_agenda, coalesce(a.dt_real, a.dt_prevista)));

if (qt_dias_pendencia_w	= 0) then
	ds_retorno_w	:= 'N';
else
				
	/*select	count(*)
	into	qt_dias_pendencia_w
	from	paciente_atendimento a,
			agenda_quimio b
	where	a.nr_seq_pend_agenda	= nr_seq_pend_agenda_p
	and		a.nr_Seq_Atendimento	= b.nr_Seq_Atendimento
	and		nvl(b.ie_Status_agenda, 'X')	<> 'C'
	and		establishment_timezone_utils.startofday(b.dt_Agenda)		= establishment_timezone_utils.startofday(nvl(a.dt_prevista_agenda, nvl(a.dt_real, a.dt_prevista)));
				
	if	(qt_dias_pendencia_w	= 0) then*/
	
		select	sum(CASE WHEN coalesce(dt_suspensao::text, '') = '' THEN  1  ELSE 0 END ),
				sum(CASE WHEN coalesce(dt_suspensao::text, '') = '' THEN  0  ELSE 1 END )
		into STRICT	qt_dias_w,
				qt_dias_susp_medico_W
		from	paciente_atendimento a
		where	a.nr_seq_pend_agenda	= nr_seq_pend_agenda_p;
			
		/*select	count(*)
		into	qt_dias_susp_medico_w
		from	paciente_atendimento
		where	nr_seq_pend_agenda	= nr_seq_pend_agenda_p
		and		dt_suspensao_medico is not null;*/
		if (qt_dias_susp_medico_w	= qt_dias_w) or (qt_dias_pendencia_w	= qt_dias_w) then
			ds_retorno_w	:= 'S';
		elsif (qt_dias_w	> qt_dias_pendencia_w) and (qt_dias_pendencia_w	> 0) then
			ds_retorno_w	:= 'A';
		elsif (qt_dias_pendencia_w = 0) then
			ds_retorno_w	:= 'N';
		else
			ds_retorno_w	:= 'C';
		end if;
	--end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
