CREATE OR REPLACE FUNCTION qt_obter_tempo_dia ( nr_seq_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_hora_aplicacao_w	smallint;
qt_tempo_aplic_soluc_w	double precision;
qt_min_aplicacao_w	smallint;

posicao_w		bigint;
ds_retorno_w		bigint;
qt_duracao_w		bigint;
ds_dia_ciclo_w		varchar(5);


BEGIN

select	sum(coalesce(qt_hora_aplicacao,0)),
	sum(coalesce(qt_min_aplicacao,0))
into STRICT	qt_hora_aplicacao_w,
	qt_min_aplicacao_w
from	paciente_atend_medic
where	nr_seq_atendimento	= nr_seq_atendimento_p
and	coalesce(nr_seq_diluicao::text, '') = ''
and	coalesce(nr_seq_solucao::text, '') = ''
order by 1;

select	sum(coalesce(qt_tempo_aplicacao,0))
into STRICT	qt_tempo_aplic_soluc_w
from	paciente_atend_soluc
where	nr_seq_atendimento	= nr_seq_atendimento_p;

qt_duracao_w	:= qt_min_aplicacao_w + (coalesce(qt_hora_aplicacao_w,0) * 60) + (coalesce(qt_tempo_aplic_soluc_w,0) * 60);

ds_retorno_w	:= qt_duracao_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
