CREATE OR REPLACE FUNCTION qt_obter_total_agend (dt_agenda_p timestamp, cd_estab_p bigint, nr_seq_grupo_quimio_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	bigint;			
			

BEGIN

select	count(*)
into STRICT	qt_retorno_w
from	agenda_quimio a,
	qt_local b
where	a.nr_seq_local = b.nr_sequencia
and	a.dt_agenda between dt_agenda_p and trunc(dt_agenda_p) + 86399/86400
and	a.ie_status_agenda <> 'C'
and	((b.cd_estabelecimento = cd_estab_p) or (coalesce(cd_estab_p::text, '') = ''))
and	((b.nr_seq_grupo_quimio = nr_seq_grupo_quimio_p) or (coalesce(nr_seq_grupo_quimio_p, 0) = 0));

return	qt_retorno_w;

end;
