CREATE OR REPLACE FUNCTION qt_obter_total_dose_ordem (nr_seq_ordem_p bigint, nr_seq_ficha_tecnica_p bigint, cd_unidade_medida_p text) RETURNS bigint AS $body$
DECLARE


qt_dose_w	double precision;


BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (nr_seq_ficha_tecnica_p IS NOT NULL AND nr_seq_ficha_tecnica_p::text <> '') and (cd_unidade_medida_p IS NOT NULL AND cd_unidade_medida_p::text <> '')	then

	select 	sum(qt_dose)
	into STRICT	qt_dose_w
	from	can_ordem_item_prescr a
	where	a.nr_seq_ordem		=	nr_seq_ordem_p
	and		a.cd_unidade_medida	=	cd_unidade_medida_p
	and		a.cd_material			in (SELECT cd_material x
										from	material x
										where 	x.nr_seq_ficha_tecnica = nr_seq_ficha_tecnica_p);

end if;

return	qt_dose_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
