CREATE OR REPLACE FUNCTION qt_obter_turno_horario (nr_seq_local_p bigint, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE


hr_quebra_turno_w	varchar(05);
hr_quebra_turno_not_w	varchar(05);
vl_retorno_w	bigint;


BEGIN

select	max(coalesce(hr_quebra_turno, '12')),
	max(hr_quebra_noturno)
into STRICT	hr_quebra_turno_w,
	hr_quebra_turno_not_w
from	qt_local
where	nr_sequencia = nr_seq_local_p;

if	(((to_char(dt_agenda_p, 'hh24'))::numeric  > somente_numero(hr_quebra_turno_w)) or ((to_char(dt_agenda_p, 'hh24'))::numeric  = somente_numero(hr_quebra_turno_w))) then
	vl_retorno_w	:= 1;
else
	vl_retorno_w	:= 0;
end if;
if (hr_quebra_turno_not_w IS NOT NULL AND hr_quebra_turno_not_w::text <> '') and ((to_char(dt_agenda_p, 'hh24'))::numeric  >= somente_numero(hr_quebra_turno_not_w)) then
	vl_retorno_w	:= 3;
end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
