CREATE OR REPLACE FUNCTION qt_se_bloq_dia_posterior (nr_seq_grupo_quimio_p bigint, ie_tipo_pend_agenda_p text, hr_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w	regra_bloqueio_quimio.nr_sequencia%type;
hr_bloqueio_w	regra_bloqueio_quimio.hr_bloqueio%type;

hr_agenda_w	timestamp;
hr_bloq_w	timestamp;

ie_retorno_w	varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_bloqueio_quimio
	where	to_date(to_char(clock_timestamp(),'dd/mm/yyyy') || ' ' || to_char(clock_timestamp(),'hh24:mi') || ':00','dd/mm/yyyy hh24:mi:ss') between
		to_date(to_char(clock_timestamp(),'dd/mm/yyyy') || ' ' || to_char(hr_inicio_agendamento,'hh24:mi') || ':00','dd/mm/yyyy hh24:mi:ss') and
		to_date(to_char(clock_timestamp(),'dd/mm/yyyy') || ' ' || to_char(hr_fim_agendamento,'hh24:mi') || ':00','dd/mm/yyyy hh24:mi:ss')
	and	((nr_seq_grupo_quimio = nr_seq_grupo_quimio_p) or (coalesce(nr_seq_grupo_quimio::text, '') = ''))
	and	((ie_tipo_pend_agenda = ie_tipo_pend_agenda_p) or (coalesce(ie_tipo_pend_agenda::text, '') = ''))
	and	((cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento) or (coalesce(cd_estabelecimento::text, '') = ''))
	order by coalesce(cd_estabelecimento,0),
		 coalesce(nr_seq_grupo_quimio,0),
		 coalesce(ie_tipo_pend_agenda,0);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_regra_w	:= nr_seq_regra_w;
	end;
end loop;
close C01;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	select	max(hr_bloqueio)
	into STRICT	hr_bloqueio_w
	from	regra_bloqueio_quimio
	where	nr_sequencia = nr_seq_regra_w;

	hr_agenda_w	:= to_date(to_char(hr_agenda_p,'dd/mm/yyyy') || ' ' || to_char(hr_agenda_p,'hh24:mi') || ':00','dd/mm/yyyy hh24:mi:ss');
	hr_bloq_w	:= to_date(to_char(clock_timestamp() + interval '1 days','dd/mm/yyyy') || ' ' || to_char(hr_bloqueio_w,'hh24:mi') || ':00','dd/mm/yyyy hh24:mi:ss');

	if (hr_agenda_p >= clock_timestamp()) and (hr_agenda_w < hr_bloq_w) then
		ie_retorno_w := 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
