CREATE OR REPLACE FUNCTION qt_valida_materiais_prescr (nr_prescricao_p bigint, nr_seq_ordem_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w		varchar(1);
qt_material_prescr_w	bigint;
qt_material_util_w	bigint;


BEGIN
ie_retorno_w	:=	'S';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then


	select 	coalesce(sum(qt_dose),0)
	into STRICT 	qt_material_prescr_w
	from	prescr_material
	where 	nr_prescricao	=	nr_prescricao_p
	and	cd_material	=	cd_material_p;

	select 	coalesce(sum(QT_DOSE_REAL),0) + 1
	into STRICT 	qt_material_util_w
	from	can_ordem_prod_mat
	where 	nr_seq_ordem	=	nr_seq_ordem_p
	and	cd_material	=	cd_material_p;

	if (qt_material_prescr_w < qt_material_util_w) then
		ie_retorno_w := 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
