CREATE OR REPLACE FUNCTION qt_verifica_se_mes_tratamento (nr_seq_pend_agenda_p bigint, dt_parametro_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_possui_tratamento_w varchar(1);


BEGIN

if (nr_seq_pend_agenda_p > 0) then

	  select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	  into STRICT ie_possui_tratamento_w
	  from  paciente_atendimento a
	  where  a.nr_seq_pend_Agenda   = nr_seq_pend_agenda_p
	  and  to_char(coalesce(dt_real,dt_prevista),'MM') = to_char(dt_parametro_p,'MM');
end if;

return ie_possui_tratamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
