CREATE OR REPLACE FUNCTION qt_verifica_turno ( nr_seq_local_p bigint, dt_agenda_p timestamp, nr_duracao_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_feriado_w	varchar(1);
ie_dia_Semana_w	integer;
qt_bloqueio_w	bigint;
qt_turno_w		bigint;
ie_turno_w	varchar(1) := 'N';
dt_agenda_w		varchar(10);
dt_agenda_ww		varchar(10);
nr_hora_ini_w		double precision;
nr_hora_fim_w		double precision;




BEGIN

ie_dia_semana_w	:= obter_cod_dia_semana(dt_agenda_p);

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_feriado_w
from 	feriado a
where 	a.cd_estabelecimento 	= wheb_usuario_pck.get_cd_estabelecimento
and	a.dt_feriado between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 86399/86400;

dt_agenda_w := to_char(dt_agenda_p, 'hh24:mi:ss');
dt_agenda_ww := to_char((dt_agenda_p + nr_duracao_p /1440), 'hh24:mi:ss');

select	count(*)
into STRICT	qt_turno_w
from	qt_local_turno a
where	a.nr_seq_local	= nr_seq_local_p
	and (dt_agenda_w between to_char(a.HR_INICIAL,'hh24:mi:ss') and to_char(a.HR_FINAL,'hh24:mi:ss')
	and dt_agenda_ww between to_char(a.HR_INICIAL,'hh24:mi:ss') and to_char(a.HR_FINAL,'hh24:mi:ss'))
and	((a.dt_dia_semana = ie_dia_semana_w) or ((a.dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))))
and	((ie_feriado_w	<> 'S' and coalesce(a.ie_feriado,'N') = 'N') or (coalesce(a.ie_feriado, 'N') = 'S' and ie_feriado_w = 'S'))
and	((coalesce(a.dt_vigencia_inicial::text, '') = '') or (a.dt_vigencia_inicial <= clock_timestamp()))
and	((coalesce(a.dt_vigencia_final::text, '') = '') or (a.dt_vigencia_final   >= clock_timestamp()))	;

if (qt_turno_w	> 0) then

	nr_hora_ini_w		:= (dt_agenda_p - trunc(dt_agenda_p))::numeric;
	nr_hora_fim_w		:= to_number((dt_agenda_p + (nr_duracao_p -1) /1440) - trunc(dt_agenda_p));

	select	count(*)
	into STRICT	qt_bloqueio_w
	from	qt_local_turno a
	where	a.nr_seq_local	= nr_seq_local_p
	and (dt_agenda_w between to_char(a.HR_INICIAL,'hh24:mi:ss') and to_char(a.HR_FINAL,'hh24:mi:ss')
	and dt_agenda_ww between to_char(a.HR_INICIAL,'hh24:mi:ss') and to_char(a.HR_FINAL,'hh24:mi:ss'))
	and	((a.dt_dia_semana = ie_dia_semana_w) or ((a.dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))))
	and	((ie_feriado_w	<> 'S' and coalesce(a.ie_feriado,'N') = 'N') or (coalesce(a.ie_feriado, 'N') = 'S' and ie_feriado_w = 'S'))
	and	((coalesce(a.dt_vigencia_inicial::text, '') = '') or (a.dt_vigencia_inicial <= clock_timestamp()))
	and	((coalesce(a.dt_vigencia_final::text, '') = '') or (a.dt_vigencia_final   >= clock_timestamp()))
	and exists 	(		SELECT	1
					from	qt_bloqueio b
					where	b.nr_seq_local 	= a.nr_seq_local
					and		trunc(dt_agenda_p) between b.dt_inicial  and b.dt_final
					and		(((((b.hr_inicio_bloqueio - trunc(b.hr_inicio_bloqueio))::numeric  <= nr_hora_ini_w) or coalesce(b.hr_inicio_bloqueio::text, '') = '')
					and		((to_number(b.hr_final_bloqueio  - trunc(b.hr_final_bloqueio) -(1/14400))  >= nr_hora_ini_w) or coalesce(b.hr_final_bloqueio::text, '') = '')
					or		(((b.hr_inicio_bloqueio - trunc(b.hr_inicio_bloqueio))::numeric  <= nr_hora_fim_w) or coalesce(b.hr_inicio_bloqueio::text, '') = '')
					and		((to_number(b.hr_final_bloqueio  - trunc(b.hr_final_bloqueio) -(1/14400))  >= nr_hora_fim_w) or coalesce(b.hr_final_bloqueio::text, '') = ''))
					or 		((to_date(to_char(b.hr_inicio_bloqueio, 'hh24:mi:ss'), 'hh24:mi:ss') between to_date(to_char(dt_agenda_p, 'hh24:mi:ss'), 'hh24:mi:ss') and  to_date(to_char((dt_agenda_p + (nr_duracao_p -1) /1440), 'hh24:mi:ss'), 'hh24:mi:ss')
					or		to_date(to_char(b.hr_final_bloqueio, 'hh24:mi:ss'), 'hh24:mi:ss') -(1/14400) between to_date(to_char(dt_agenda_p, 'hh24:mi:ss'), 'hh24:mi:ss') and  to_date(to_char((dt_agenda_p + (nr_duracao_p -1) /1440), 'hh24:mi:ss'), 'hh24:mi:ss')
					or 		to_date(to_char(dt_agenda_p, 'hh24:mi:ss'), 'hh24:mi:ss') between to_date(to_char(b.hr_inicio_bloqueio, 'hh24:mi:ss'), 'hh24:mi:ss') and (to_date(to_char(b.hr_final_bloqueio, 'hh24:mi:ss'), 'hh24:mi:ss') - (1/14400))
					or 		to_date(to_char((dt_agenda_p + (nr_duracao_p -1) /1440), 'hh24:mi:ss'), 'hh24:mi:ss') between to_date(to_char(b.hr_inicio_bloqueio, 'hh24:mi:ss'), 'hh24:mi:ss') and (to_date(to_char(b.hr_final_bloqueio, 'hh24:mi:ss'), 'hh24:mi:ss') - (1/14400))
					) or coalesce(b.hr_final_bloqueio::text, '') = '' and coalesce(b.hr_inicio_bloqueio::text, '') = ''))
					and		(((b.dt_dia_semana = ie_dia_semana_w) or ((b.dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1)))) or (coalesce(dt_dia_semana::text, '') = ''))
			);
end if;

if (qt_turno_w > 0 and qt_bloqueio_w = 0) then
	ie_turno_w := 'S';
end if;

return	ie_turno_w;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
