SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qua_doc_obter_novo_status (nr_seq_doc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_w			varchar(1);
qt_reg_doc_rev_w	bigint;
dt_validacao_w		timestamp;
dt_aprovacao_w		timestamp;


BEGIN

select	ie_status,
		dt_validacao,
		dt_aprovacao
into STRICT	ds_status_w,
		dt_validacao_w,
		dt_aprovacao_w
from	qua_documento
where	nr_sequencia = nr_seq_doc_p;

select	count(*) qt_doc_revisao
into STRICT	qt_reg_doc_rev_w
from	qua_doc_revisao
where	nr_seq_doc = nr_seq_doc_p
and	coalesce(dt_aprovacao::text, '') = '';

if (qt_reg_doc_rev_w > 0) then
	ds_status_w := 'R';
elsif (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
	ds_status_w := 'D';
elsif (dt_validacao_w IS NOT NULL AND dt_validacao_w::text <> '') then
	ds_status_w := 'V';
elsif (dt_validacao_w IS NOT NULL AND dt_validacao_w::text <> '') then
	ds_status_w := 'E';
else
	ds_status_w := 'P';
end if;

return	ds_status_w	;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
