CREATE OR REPLACE FUNCTION qua_is_evento_atrasado ( nr_seq_evento_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2);


BEGIN

select	CASE WHEN x.cont=0 THEN  'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from (	SELECT 	count(*) cont
		from	qua_evento_paciente a,
			qua_gravidade_evento b
		where	a.nr_seq_gravidade = b.nr_sequencia
		and	a.nr_sequencia = nr_seq_evento_p
		and	a.ie_status <> '5'
		and (clock_timestamp() - a.dt_cadastro) > b.qt_dias	) x;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
