CREATE OR REPLACE FUNCTION qua_obter_acao_aguard_eficacia ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	select	'S'
	into STRICT	ie_retorno_w
	from	man_ordem_servico
	where	(cd_pf_eficacia IS NOT NULL AND cd_pf_eficacia::text <> '')
	and	(qt_dia_analise_eficacia_acao IS NOT NULL AND qt_dia_analise_eficacia_acao::text <> '')
	and	(dt_prev_eficacia IS NOT NULL AND dt_prev_eficacia::text <> '')
	and	coalesce(ie_eficacia::text, '') = ''
	and	nr_sequencia = nr_sequencia_p  LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
