CREATE OR REPLACE FUNCTION qua_obter_auditoria_rnc ( nr_seq_nao_conform_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_auditoria_w		bigint;
ds_retorno_w			varchar(255);


BEGIN

if (nr_seq_nao_conform_p > 0) then
	begin

	select	max(a.nr_seq_auditoria)
	into STRICT	nr_seq_auditoria_w
	from	qua_auditoria_result a,
		qua_audit_result_resp b
	where	a.nr_sequencia = b.nr_seq_result_audit
	and	b.nr_seq_nao_conform = nr_seq_nao_conform_p;

	if (ie_retorno_p = 'D') and (coalesce(nr_seq_auditoria_w,0) > 0) then
		begin

		select	a.ds_tipo
		into STRICT	ds_retorno_w
		from	qua_auditoria_tipo a,
			qua_auditoria b
		where	a.nr_sequencia = b.nr_seq_tipo
		and	b.nr_sequencia = nr_seq_auditoria_w;

		end;
	else
		ds_retorno_w	:= nr_seq_auditoria_w;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
