CREATE OR REPLACE FUNCTION qua_obter_dados_tipo_doc ( nr_seq_tipo_doc_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

/*
1 - Desc tipo documento
2 - Cor fonte
3 - cor fundo
4 - Sigla
*/
ds_retorno_w		varchar(60);
ds_tipo_doc_w		varchar(60);
ds_cor_fonte_w		varchar(15);
ds_cor_fundo_w		varchar(15);
ds_sigla_tipo_doc_w	varchar(20);


BEGIN

select	ds_tipo_doc,
	ds_cor_fonte,
	ds_cor_fundo,
	ds_sigla_tipo_doc
into STRICT	ds_tipo_doc_w,
	ds_cor_fonte_w,
	ds_cor_fundo_w,
	ds_sigla_tipo_doc_w
from	qua_tipo_doc
where	nr_sequencia = nr_seq_tipo_doc_p;

if (ie_opcao_p = 1) then
	ds_retorno_w := ds_tipo_doc_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := ds_cor_fonte_w;
elsif (ie_opcao_p = 3) then
	ds_retorno_w := ds_cor_fundo_w;
elsif (ie_opcao_p = 4) then
	ds_retorno_w := ds_sigla_tipo_doc_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
