CREATE OR REPLACE FUNCTION qua_obter_desc_tipo_doc ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tipo_doc_w			varchar(60);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	coalesce(max(ds_tipo_doc),'')
	into STRICT	ds_tipo_doc_w
	from	qua_tipo_doc
	where	nr_sequencia	= nr_sequencia_p;
end if;

return	ds_tipo_doc_w;

end;
