CREATE OR REPLACE FUNCTION qua_obter_estrut_regra (nr_seq_estrutura_p bigint, cd_setor_atendimento_p bigint, nr_seq_ambiente_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w	integer;
