	where	nr_seq_estrutura = nr_seq_estrutura_p;

	if (qt_reg_w > 0) then
		select  coalesce(nr_seq_estrutura,0)
		into STRICT	nr_sequencia_w
		from	qua_auditoria_estrut_regra
		where	nr_seq_estrutura = nr_seq_estrutura_p
		and	nr_seq_ambiente = nr_seq_ambiente_p;
	else
		nr_sequencia_w	:= coalesce(nr_seq_estrutura_p,0);
	end if;
	end;
end if;

if (coalesce(nr_seq_ambiente_p,0) <> 0) and (coalesce(cd_setor_atendimento_p,0) <> 0) then
	begin
	select	count(1)
	into STRICT	qt_reg_w
	from	qua_auditoria_estrut_regra
	where	nr_seq_estrutura = nr_seq_estrutura_p;

	if (qt_reg_w > 0) then
		select  coalesce(nr_seq_estrutura,0)
