CREATE OR REPLACE FUNCTION qua_obter_inf_adic_item_html ( nr_seq_evento_p bigint, nr_seq_item_p bigint, ie_tipo_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN


select	max(CASE WHEN ie_tipo_campo_p='N' THEN to_char(nr_inf_adic) WHEN ie_tipo_campo_p='A' THEN ds_inf_adic WHEN ie_tipo_campo_p='D' THEN dt_inf_adic END )
into STRICT	ds_retorno_w
from	QUA_EV_PAC_INF_ADIC a,
		QUA_EVENTO_INF_ADIC b
where	a.nr_seq_evento	= nr_seq_evento_p
and	   a.NR_SEQ_INF_ADIC	= nr_seq_item_p
and	b.nr_sequencia = a.NR_SEQ_INF_ADIC;

return		ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
